export function swiperAnimateCache(a) {
  const length = a.slides.length
  for (let j = 0; j < length; j++) {
    const allBoxes = a.slides[j].querySelectorAll(".ani")
    for (let i = 0; i < allBoxes.length; i++) {
      if (allBoxes[i].attributes["style"]) {
        const value = allBoxes[i].attributes["style"].value;
        allBoxes[i].setAttribute("swiper-animate-style-cache", value);
      } else {
        allBoxes[i].setAttribute("swiper-animate-style-cache", " ");
        allBoxes[i].style.visibility = "hidden";
      }
    }
  }
}
export function swiperAnimate(a) {
  clearSwiperAnimate(a);
  var b = a.slides[a.activeIndex].querySelectorAll(".ani");
  for (let i = 0; i < b.length; i++) {
    b[i].style.visibility = "visible";

    const effect = b[i].attributes["swiper-animate-effect"]
      ? b[i].attributes["swiper-animate-effect"].value
      : "";

    b[i].className = b[i].className + "  " + effect + " " + "animated";

    let style = b[i].attributes["style"].value;

    const duration = b[i].attributes["swiper-animate-duration"]
      ? b[i].attributes["swiper-animate-duration"].value
      : "";

    if (duration) {
      style = style + "animation-duration:" + duration + ";-webkit-animation-duration:" + duration + ";";
    }

    const delay = b[i].attributes["swiper-animate-delay"]
      ? b[i].attributes["swiper-animate-delay"].value
      : "";

    if (delay) {
      style = style + "animation-delay:" + delay + ";-webkit-animation-delay:" + delay + ";"
    }

    b[i].setAttribute("style", style);
  }
}

function clearSwiperAnimate(a) {
  for (let j = 0; j < a.slides.length; j++) {
    const allBoxes = a.slides[j].querySelectorAll(".ani");
    for (let i = 0; i < allBoxes.length; i++) {
      if (allBoxes[i].attributes["swiper-animate-style-cache"]) {
        const value = allBoxes[i].attributes["swiper-animate-style-cache"].value;
        allBoxes[i].setAttribute("style", value);
      }
      allBoxes[i].style.visibility = "hidden";

      allBoxes[i].className = allBoxes[i].className.replace("animated", " ");

      const effect = allBoxes[i].attributes["swiper-animate-effect"];
      if (effect && effect.value) {
        allBoxes[i].className = allBoxes[i].className.replace(effect.value, " ")
      }
    }

  }
}
